INSERT INTO `uc_information_description` VALUES ('9', '2', 'Typography', '&lt;link rel=&quot;stylesheet&quot; href=&quot;//cdnjs.cloudflare.com/ajax/libs/highlight.js/9.12.0/styles/default.min.css&quot;&gt; \n&lt;script src=&quot;//cdnjs.cloudflare.com/ajax/libs/highlight.js/9.12.0/highlight.min.js&quot;&gt;&lt;/script&gt; \n&lt;script src=&quot;https://cdnjs.cloudflare.com/ajax/libs/clipboard.js/1.7.1/clipboard.min.js&quot;&gt;&lt;/script&gt; \n&lt;script src=&quot;catalog/view/javascript/soconfig/js/typo/application.js&quot;&gt;&lt;/script&gt; \n \n \n&lt;!-- CORE TYPOGRAPHY \n=======================================================--&gt; \n&lt;div class=&quot;docs-section&quot;&gt; \n&lt;div class=&quot;page-header&quot;&gt; \n &lt;h1 id=&quot;example-type&quot;&gt;Core Typography&lt;/h1&gt; \n&lt;/div&gt; \n&lt;!-- Headings --&gt; \n \n&lt;div class=&quot;row example-row example-type&quot;&gt; \n &lt;div class=&quot;col-lg-6&quot;&gt; \n &lt;h2 class=&quot;sub-title&quot;&gt;Headings&lt;/h2&gt; \n &lt;p class=&quot;description&quot;&gt;All HTML headings, &lt;code&gt;&amp;lt;h1&amp;gt;&lt;/code&gt; through &lt;code&gt;&amp;lt;h6&amp;gt;&lt;/code&gt;, are available.&lt;/p&gt; \n &lt;div class=&quot;bd-example&quot; data-example-id=&quot;&quot;&gt; \n &lt;p class=&quot;h1&quot;&gt;h1. Bootstrap heading&lt;/p&gt; \n &lt;p class=&quot;h2&quot;&gt;h2. Bootstrap heading&lt;/p&gt; \n &lt;p class=&quot;h3&quot;&gt;h3. Bootstrap heading&lt;/p&gt; \n &lt;p class=&quot;h4&quot;&gt;h4. Bootstrap heading&lt;/p&gt; \n &lt;p class=&quot;h5&quot;&gt;h5. Bootstrap heading&lt;/p&gt; \n &lt;p class=&quot;h6&quot;&gt;h6. Bootstrap heading&lt;/p&gt; \n &lt;/div&gt; \n &lt;div class=&quot;highlight&quot;&gt;&lt;pre&gt;&lt;code class=&quot;language-html&quot; data-lang=&quot;html&quot;&gt; &lt;span class=&quot;nt&quot;&gt;&amp;lt;p&lt;/span&gt; &lt;span class=&quot;na&quot;&gt;class=&lt;/span&gt;&lt;span class=&quot;s&quot;&gt;&quot;h1&quot;&lt;/span&gt;&lt;span class=&quot;nt&quot;&gt;