INSERT INTO `uc_modification` VALUES ('21', '0', 'So Placeholder', 'ocmod-soplaceholder', 'OpencartWorks', '1.0.0', 'http://www.OpencartWorks.com', '<?xml version=\"1.0\" encoding=\"utf-8\"?> \n<modification> \n <name><![CDATA[So Placeholder]]></name> \n <code>ocmod-soplaceholder</code> \n <version><![CDATA[1.0.0]]></version> \n <author><![CDATA[OpencartWorks]]></author> \n <link>http://www.OpencartWorks.com</link> \n \n<!--========Catalog/controller/product=============--> \n <file path=\"catalog/controller/product/category.php\"> \n <operation> \n <search><![CDATA[$image = $this->model_tool_image->resize(\'placeholder.png\', $this->config->get(\'theme_\' . $this->config->get(\'config_theme\') . \'_image_product_width\'), $this->config->get(\'theme_\' . $this->config->get(\'config_theme\') . \'_image_product_height\'));]]></search> \n <add position=\"replace\"><![CDATA[$image = $this->model_tool_image->resize($placeholder, $this->config->get(\'theme_\' . $this->config->get(\'config_theme\') . \'_image_product_width\'), $this->config->get(\'theme_\' . $this->config->get(\'config_theme\') . \'_image_product_height\'));]]></add> \n </operation> \n <operation > \n <search ><![CDATA[// Set the last category breadcrumb]]></search> \n <add position=\"before\"><![CDATA[ \n $placeholder=\'placeholder.png\'; \n if($this->soconfig->get_settings(\'placeholder_status\')){ \n $placeholder = $this->soconfig->get_settings(\'placeholder_img\'); \n }else{ \n $placeholder = \'placeholder.png\'; \n } \n ]]> \n </add> \n </operation> \n <operation > \n <search ><![CDATA[$data[\'thumb\'] = \'\';]]></search> \n <add position=\"replace\"><![CDATA[ \n $data[\'thumb\'] = $this->model_tool_image->resize($placeholder, $this->config->get(\'theme_\' . $this->config->get(\'config_theme\') . \'_image_category_width\'), $this->config->get(\'theme_\' . $this->config->get(\'config_theme\') . \'_image_category_height\')); \n ]]> \n </add> \n </operation> \n <operation > \n <search ><![CDATA[$data[\'categories\'][] ]]></search> \n <add position=\"before\"><![CDATA[ \n if ($result[\'image\']) $image = $this->model_tool_image->resize($result[\'image\'], $this->config->get(\'theme_\' .$this->config->get(\'config_theme\') . \'_image_category_width\'), $this->config->get(\'theme_\' .$this->config->get(\'config_theme\') . \'_image_category_height\')); \n else $image = $this->model_tool_image->resize($placeholder, $this->config->get(\'theme_\' .$this->config->get(\'config_theme\') . \'_image_category_width\'),$this->config->get( \'theme_\' .$this->config->get(\'config_theme\') . \'_image_category_height\')); \n ]]> \n </add> \n </operation> \n \n <operation> \n <search ><![CDATA[$data[\'categories\'][]]]></search> \n <add position=\"after\"><![CDATA[ \'thumb\' => $image,]]></add> \n </operation> \n \n \n \n </file> \n \n \n</modification> ', '1', '2018-01-04 09:04:09', '2018-01-04 09:04:13');
INSERT INTO `uc_modification` VALUES ('3', '0', 'So ThemeConfig', 'ocmod-soconfig', 'OpenCartWorks', '3.0.0', 'http://www.OpencartWorks.com', '<?xml version=\"1.0\" encoding=\"utf-8\"?> \n<modification> \n <name><![CDATA[So ThemeConfig]]></name> \n <code>ocmod-soconfig</code> \n <version><![CDATA[3.0.0]]></version> \n <author><![CDATA[OpenCartWorks]]></author> \n <link>http://www.OpencartWorks.com</link> \n \n<!--========Catalog/controller/common=============--> \n <file path=\"catalog/controller/common/language.php\"> \n <operation> \n <search ><![CDATA[$data[\'languages\'] = array();]]></search> \n <add position=\"after\"><![CDATA[ \n if (isset($this->request->get[\'lang\']) && $this->request->get[\'lang\'] == \'ar-ar\') { \n $this->session->data[\'language\'] = $this->request->get[\'lang\']; \n $this->response->redirect($this->url->link(\'common/home\')); \n } \n ]]></add> \n </operation> \n </file> \n \n <file path=\"catalog/controller/common/cart.php\"> \n <operation > \n <search ><![CDATA[public function index()]]></search> \n <add position=\"after\" offset=\"2\"><![CDATA[ \n /*======Show Themeconfig=======*/ \n $this->load->language(\'extension/soconfig/cart\'); \n $data[\'objlang\'] = $this->language; \n ]]></add> \n </operation> \n </file> \n \n <file path=\"catalog/controller/common/header.php\"> \n <operation > \n <search ><![CDATA[public function index()]]></search> \n <add position=\"after\"><![CDATA[ \n /*======Show Themeconfig=======*/ \n $data[\'soconfig\'] = $this->soconfig; \n $this->load->language(\'extension/soconfig/compare\'); \n $data[\'objlang\'] = $this->language; \n $data[\'lang_id\'] = $this->config->get(\'config_language_id\'); \n $data[\'theme_directory\'] = $this->config->get(\'theme_default_directory\'); \n $data[\'url_layoutbox\'] = isset($this->request->get[\'layoutbox\']) ? $this->request->get[\'layoutbox\'] : \'\' ; \n $data[\'url_pattern\'] = isset($this->request->get[\'pattern\']) ? $this->request->get[\'pattern\'] : \'\' ; \n $data[\'account_fb\'] = isset($this->request->get[\'account_fb\']) ? $this->request->get[\'account_fb\'] : \'\' ; \n $data[\'compare\'] = $this->url->link(\'product/compare\', \'\', true); \n \n // add position \n $data[\'content_menu1\'] = $this->load->controller(\'extension/soconfig/content_menu1\'); \n $data[\'content_menu2\'] = $this->load->controller(\'extension/soconfig/content_menu2\'); \n $data[\'header_block\'] = $this->load->controller(\'extension/soconfig/header_block\'); \n $data[\'search_block\'] = $this->load->controller(\'extension/soconfig/search_block\'); \n \n // For page specific css \n if (isset($this->request->get[\'route\'])) $data[\'class\'] = str_replace(\'/\', \'-\', $this->request->get[\'route\']); \n else $data[\'class\'] = \'common-home\'; \n \n //Decodes HTML Entities \n $data[\'selector_body\'] = html_entity_decode($data[\'soconfig\']->get_settings(\'selector_body\'), ENT_QUOTES, \'UTF-8\'); \n $data[\'selector_menu\'] = html_entity_decode($data[\'soconfig\']->get_settings(\'selector_menu\'), ENT_QUOTES, \'UTF-8\'); \n $data[\'selector_heading\'] = html_entity_decode($data[\'soconfig\']->get_settings(\'selector_heading\'), ENT_QUOTES, \'UTF-8\'); \n $data[\'mselector_body\'] = html_entity_decode($data[\'soconfig\']->get_settings(\'mselector_body\'), ENT_QUOTES, \'UTF-8\'); \n $data[\'mselector_menu\'] = html_entity_decode($data[\'soconfig\']->get_settings(\'mselector_menu\'), ENT_QUOTES, \'UTF-8\'); \n $data[\'mselector_heading\'] = html_entity_decode($data[\'soconfig\']->get_settings(\'mselector_heading\'), ENT_QUOTES, \'UTF-8\'); \n \n if (!defined (\'OWL_CAROUSEL\')){ \n $this->document->addStyle(\'catalog/view/javascript/soconfig/css/owl.carousel.css\'); \n $this->document->addScript(\'catalog/view/javascript/soconfig/js/owl.carousel.js\'); \n define( \'OWL_CAROUSEL\', 1 ); \n } \n ]]></add> \n </operation> \n </file> \n \n <file path=\"catalog/controller/common/footer.php\"> \n <operation > \n <search ><![CDATA[public function index()]]></search> \n <add position=\"after\"><![CDATA[ \n /*======Show Themeconfig=======*/ \n $data[\'soconfig\'] = $this->soconfig; \n $this->load->language(\'extension/soconfig/soconfig\'); \n $data[\'objlang\'] = $this->language; \n $data[\'lang_id\'] = $this->config->get(\'config_language_id\'); \n $data[\'theme_directory\'] = $this->config->get(\'theme_default_directory\'); \n $data[\'account_fb\'] = isset($this->request->get[\'account_fb\']) ? $this->request->get[\'account_fb\'] : \'\' ; \n $data[\'compare\'] = $this->url->link(\'product/compare\', \'\', true); \n \n // add position \n if( $this->soconfig->get_settings(\'typefooter\') == 1) $data[\'footer_block1\'] = $this->load->controller(\'extension/soconfig/footer_block1\'); \n else if( $this->soconfig->get_settings(\'typefooter\') == 2) $data[\'footer_block2\'] = $this->load->controller(\'extension/soconfig/footer_block2\'); \n else if( $this->soconfig->get_settings(\'typefooter\') == 3) $data[\'footer_block3\'] = $this->load->controller(\'extension/soconfig/footer_block3\'); \n \n \n ]]></add> \n </operation> \n </file> \n <file path=\"catalog/controller/common/maintenance.php\"> \n <operation > \n <search ><![CDATA[public function index()]]></search> \n <add position=\"after\"><![CDATA[ \n /*======Show Themeconfig=======*/ \n $this->load->language(\'extension/soconfig/soconfig\'); \n $data[\'objlang\'] = $this->language; \n $data[\'soconfig\'] = $this->soconfig; \n if ($this->request->server[\'HTTPS\']) $server = $this->config->get(\'config_ssl\'); \n else $server = $this->config->get(\'config_url\'); \n $data[\'config_logo\'] = $server.\'image/\'.$this->config->get(\'config_logo\'); \n $data[\'comingsoon_content\'] = html_entity_decode($data[\'soconfig\']->get_settings(\'comingsoon_content\'), ENT_QUOTES, \'UTF-8\'); \n $data[\'content_comingsoon\'] = $this->load->controller(\'extension/soconfig/content_soon\'); \n ]]></add> \n </operation> \n \n </file> \n <file path=\"catalog/controller/common/home.php\"> \n <operation > \n <search ><![CDATA[$data[\'header\'] = $this->load->controller(\'common/header\');]]></search> \n <add position=\"before\"><![CDATA[ \n $this->load->language(\'extension/soconfig/soconfig\'); \n $data[\'objlang\'] = $this->language; \n ]]></add> \n </operation> \n \n </file> \n <file path=\"catalog/controller/common/home.php\"> \n <operation > \n <search ><![CDATA[public function index()]]></search> \n <add position=\"after\"><![CDATA[ \n $data[\'content_home\'] = $this->load->controller(\'extension/soconfig/content_home\'); \n ]]></add> \n </operation> \n </file> \n<!-- ==========catalog/controller/product=========--> \n <file path=\"catalog/controller/product/{search,special,manufacturer,category}*.php\"> \n <operation> \n <search ><![CDATA[public function index()]]></search> \n <add position=\"after\"><![CDATA[ \n /*=======Show Themeconfig=======*/ \n $this->load->model(\'extension/soconfig/general\'); \n $this->load->language(\'extension/soconfig/soconfig\'); \n $data[\'objlang\'] = $this->language; \n $data[\'soconfig\'] = $this->soconfig; \n $data[\'theme_directory\'] = $this->config->get(\'theme_default_directory\'); \n $data[\'our_url\'] = $this->registry->get(\'url\'); \n /*=======url query parameters=======*/ \n $data[\'url_sidebarsticky\'] = isset($this->request->get[\'sidebarsticky\']) ? $this->request->get[\'sidebarsticky\'] : \'\' ; \n $data[\'url_cartinfo\'] = isset($this->request->get[\'cartinfo\']) ? $this->request->get[\'cartinfo\'] : \'\' ; \n $data[\'url_thumbgallery\'] = isset($this->request->get[\'thumbgallery\']) ? $this->request->get[\'thumbgallery\'] : \'\' ; \n $data[\'url_listview\'] = isset($this->request->get[\'listview\']) ? $this->request->get[\'listview\'] : \'\' ; \n $data[\'url_asidePosition\'] = isset($this->request->get[\'asidePosition\']) ? $this->request->get[\'asidePosition\'] : \'\' ; \n $data[\'url_asideType\'] = isset($this->request->get[\'asideType\']) ? $this->request->get[\'asideType\'] : \'\' ; \n $data[\'url_layoutbox\'] = isset($this->request->get[\'layoutbox\']) ? $this->request->get[\'layoutbox\'] : \'\' ; \n ]]></add> \n </operation> \n \n <operation> \n <search ><![CDATA[$data[\'products\'][] = array(]]></search> \n <add position=\"before\"><![CDATA[ \n /*======Image Galleries=======*/ \n $data[\'image_galleries\'] = array(); \n $image_galleries = $this->model_catalog_product->getProductImages($result[\'product_id\']); \n foreach ($image_galleries as $image_gallery) { \n $data[\'image_galleries\'][] = array( \n \'cart\' => $this->model_tool_image->resize($image_gallery[\'image\'], $this->config->get(\'theme_\' . $this->config->get(\'config_theme\') . \'_image_cart_width\'), $this->config->get(\'theme_\' . $this->config->get(\'config_theme\') . \'_image_cart_width\')), \n \'thumb\' => $this->model_tool_image->resize($image_gallery[\'image\'], $this->config->get(\'theme_\' . $this->config->get(\'config_theme\') . \'_image_product_width\'), $this->config->get(\'theme_\' . $this->config->get(\'config_theme\') . \'_image_product_width\')) \n ); \n } \n $data[\'first_gallery\'] = array( \n \'cart\' => $this->model_tool_image->resize($result[\'image\'], $this->config->get(\'theme_\' . $this->config->get(\'config_theme\') . \'_image_cart_width\'), $this->config->get(\'theme_\' . $this->config->get(\'config_theme\') . \'_image_cart_width\')), \n \'thumb\' => $this->model_tool_image->resize($result[\'image\'], $this->config->get(\'theme_\' . $this->config->get(\'config_theme\') . \'_image_product_width\'), $this->config->get(\'theme_\' . $this->config->get(\'config_theme\') . \'_image_product_width\')) \n ); \n /*======Check New Label=======*/ \n if ((float)$result[\'special\']) $discount = \'-\'.round((($result[\'price\'] - $result[\'special\'])/$result[\'price\'])*100, 0).\'%\'; \n else $discount = false; \n \n $sold = 0; \n if($this->model_extension_soconfig_general->getUnitsSold($result[\'product_id\'])){ \n $sold = $this->model_extension_soconfig_general->getUnitsSold($result[\'product_id\']); \n } \n \n $data[\'orders\'] = sprintf($this->language->get(\'text_product_orders\'),$sold); \n $data[\'reviews\'] = sprintf($this->language->get(\'text_reviews\'), (int)$result[\'reviews\']); \n ]]></add> \n </operation> \n \n <operation> \n <search><![CDATA[$data[\'products\'][] = array]]></search> \n <add position=\"after\" trim=\"true\" offset=\"2\"><![CDATA[ \n \'special_end_date\' => $this->model_extension_soconfig_general->getDateEnd($result[\'product_id\']), \n \'image_galleries\' => $data[\'image_galleries\'], \n \'first_gallery\' => $data[\'first_gallery\'], \n \'discount\' => $discount, \n \'stock_status\' => $result[\'stock_status\'], \n \'orders\' => $data[\'orders\'], \n \'reviews\' => $data[\'reviews\'], \n \'href_quickview\' => htmlspecialchars_decode($this->url->link(\'extension/soconfig/quickview&product_id=\'.$result[\'product_id\'] )), \n \'quantity\' => $result[\'quantity\'],]]></add> \n </operation> \n \n </file> \n \n <file path=\"catalog/controller/product/manufacturer.php\"> \n <operation> \n <search ><![CDATA[public function info()]]></search> \n <add position=\"after\"><![CDATA[ \n /*=======Show Themeconfig=======*/ \n $this->load->model(\'extension/soconfig/general\'); \n $this->load->language(\'extension/soconfig/soconfig\'); \n $data[\'objlang\'] = $this->language; \n $data[\'soconfig\'] = $this->soconfig; \n $data[\'theme_directory\'] = $this->config->get(\'theme_default_directory\'); \n $data[\'our_url\'] = $this->registry->get(\'url\'); \n ]]></add> \n </operation> \n </file> \n <file path=\"catalog/controller/product/product.php\"> \n <operation> \n <search ><![CDATA[public function index()]]></search> \n <add position=\"after\"><![CDATA[ \n /*=======Show Themeconfig=======*/ \n $this->load->language(\'extension/soconfig/soconfig\'); \n $data[\'objlang\'] = $this->language; \n $data[\'soconfig\'] = $this->soconfig; \n $data[\'theme_directory\'] = $this->config->get(\'theme_default_directory\'); \n $data[\'our_url\'] = $this->registry->get(\'url\'); \n $direction = $this->language->get(\'direction\'); \n $data[\'direction\'] = $this->language->get(\'direction\') == \'rtl\' ? \'yes\' : \'no\' ; \n /*=======url query parameters=======*/ \n $data[\'url_sidebarsticky\'] = isset($this->request->get[\'sidebarsticky\']) ? $this->request->get[\'sidebarsticky\'] : \'\' ; \n $data[\'url_productGallery\'] = isset($this->request->get[\'productGallery\']) ? $this->request->get[\'productGallery\'] : \'\' ; \n $data[\'url_asidePosition\'] = isset($this->request->get[\'asidePosition\']) ? $this->request->get[\'asidePosition\'] : \'\' ; \n $data[\'url_asideType\'] = isset($this->request->get[\'asideType\']) ? $this->request->get[\'asideType\'] : \'\' ; \n ]]></add> \n </operation> \n <operation error=\"skip\"> \n <search ><![CDATA[$this->document->addScript(\'catalog/view/javascript/jquery/magnific/jquery.magnific-popup.min.js\');]]></search> \n <add position=\"replace\"><![CDATA[ \n $this->document->addScript(\'catalog/view/javascript/soconfig/js/jquery.elevateZoom-3.0.8.min.js\'); \n $this->document->addScript(\'catalog/view/javascript/soconfig/js/jquery.sticky-kit.min.js\'); \n $this->document->addScript(\'catalog/view/javascript/soconfig/js/lightslider.js\'); \n ]]></add> \n </operation> \n \n <operation error=\"skip\"> \n <search ><![CDATA[$this->document->addStyle(\'catalog/view/javascript/jquery/magnific/magnific-popup.css\');]]></search> \n <add position=\"replace\"><![CDATA[$this->document->addStyle(\'catalog/view/javascript/soconfig/css/lightslider.css\');]]></add> \n </operation> \n \n <operation> \n <search ><![CDATA[$data[\'description\'] = html_entity_decode($product_info[\'description\'], ENT_QUOTES, \'UTF-8\');]]></search> \n <add position=\"after\"><![CDATA[ \n //custom code \n $this->load->model(\'extension/soconfig/general\'); \n $data[\'special_end_date\'] = $this->model_extension_soconfig_general->getDateEnd($product_id); \n $limit = 250; \n $full_description = html_entity_decode($product_info[\'description\'], ENT_QUOTES, \'UTF-8\'); \n $product_description_short = (strlen($full_description) > $limit ? utf8_substr($full_description, 0, $limit) . \'...\' : $full_description); \n $data[\'description_short\'] = strip_tags($product_description_short); \n \n //SEO Rich Snippets \n \n if ($this->customer->isLogged() || !$this->config->get(\'config_customer_price\')) { \n $data[\'price_value\']=$this->tax->calculate($product_info[\'price\'], $product_info[\'tax_class_id\'], $this->config->get(\'config_tax\')); \n } else { \n $data[\'price_value\']=0; \n } \n \n if ((float)$product_info[\'special\']) { \n $data[\'special_value\'] = $this->tax->calculate($product_info[\'special\'], $product_info[\'tax_class_id\'], $this->config->get(\'config_tax\')); \n } else { \n $data[\'special_value\'] = false; \n } \n $data[\'currency\']=$this->session->data[\'currency\']; \n $data[\'count_reviews\']=(int)$product_info[\'reviews\']; \n \n ]]></add> \n </operation> \n \n <operation> \n <search ><![CDATA[$data[\'options\'] = array();]]></search> \n <add position=\"before\"><![CDATA[ \n if ((float)$product_info[\'special\']) $data[\'discount\'] = \'-\'.round((($product_info[\'price\'] - $product_info[\'special\'])/$product_info[\'price\'])*100, 0).\'%\'; \n else $data[\'discount\'] = false; \n /*======Check New Label=======*/ \n $sold = 0; \n if($this->model_extension_soconfig_general->getUnitsSold($product_info[\'product_id\'])){ \n $sold = $this->model_extension_soconfig_general->getUnitsSold($product_info[\'product_id\']); \n } \n \n $data[\'orders\'] = sprintf($this->language->get(\'text_product_orders\'),$sold); \n /*====== insert image to the beginning of array=======*/ \n $firstimg = array(\'popup\' => $data[\'popup\'],\'thumb\' => $data[\'thumb\']); \n array_unshift($data[\'images\'], $firstimg); \n \n ]]></add> \n </operation> \n \n <operation> \n <search ><![CDATA[$data[\'products\'][] = array(]]></search> \n <add position=\"before\"><![CDATA[ \n if ((float)$result[\'special\']) $discount = \'-\'.round((($result[\'price\'] - $result[\'special\'])/$result[\'price\'])*100, 0).\'%\'; \n else $discount = false; \n ]]></add> \n </operation> \n <operation> \n <search><![CDATA[$data[\'products\'][] = array]]></search> \n <add position=\"after\" trim=\"true\" offset=\"2\"><![CDATA[ \n \'special_end_date\' => $this->model_extension_soconfig_general->getDateEnd($result[\'product_id\']), \n \'discount\' => $discount,]]></add> \n </operation> \n <operation> \n <search><![CDATA[$data[\'images\'] = array()]]></search> \n <add position=\"before\" trim=\"true\" ><![CDATA[ \n if ($product_info[\'image\']) { \n $data[\'popup\'] = $this->model_tool_image->resize($product_info[\'image\'], $this->config->get(\'theme_\' . $this->config->get(\'config_theme\') . \'_image_popup_width\'), $this->config->get(\'theme_\' . $this->config->get(\'config_theme\') . \'_image_popup_height\')); \n } else { \n $data[\'popup\'] = $this->model_tool_image->resize(\'placeholder.png\', $this->config->get(\'theme_\' . $this->config->get(\'config_theme\') . \'_image_popup_width\'), $this->config->get(\'theme_\' . $this->config->get(\'config_theme\') . \'_image_popup_height\')); \n } \n \n if ($product_info[\'image\']) { \n $data[\'thumb\'] = $this->model_tool_image->resize($product_info[\'image\'], $this->config->get(\'theme_\' . $this->config->get(\'config_theme\') . \'_image_thumb_width\'), $this->config->get(\'theme_\' . $this->config->get(\'config_theme\') . \'_image_thumb_height\')); \n } else { \n $data[\'thumb\'] = $this->model_tool_image->resize(\'placeholder.png\', $this->config->get(\'theme_\' . $this->config->get(\'config_theme\') . \'_image_thumb_width\'), $this->config->get(\'theme_\' . $this->config->get(\'config_theme\') . \'_image_thumb_height\')); \n }]]> \n </add> \n </operation> \n </file> \n \n<!-- =======system/engine/router===============--> \n <file path=\"system/engine/router.php\"> \n <operation> \n <search >