INSERT INTO `uc_modification` VALUES ('31', '2', 'Multi Vendor Marketplace', 'mpseller', 'ModulePoints', '1.0', '', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\r\n<modification>\r\n	<name>Multi Vendor Marketplace</name>\r\n	<code>mpseller</code>\r\n	<version>1.0</version>\r\n	\r\n    <author>ModulePoints</author>\r\n    <file path=\"admin/controller/common/column_left.php\">\r\n		<operation>\r\n			<search><![CDATA[// Design]]></search>\r\n			<add position=\"before\"><![CDATA[\r\n			/**** MP Seller Starts ****/\r\n			if(VERSION >= \'2.1.0.2\') {\r\n				$ssl = true;\r\n			} else{\r\n				$ssl = \'SSL\';\r\n			}\r\n\r\n			// Multi-Vendor\r\n			$mpmultivendor = array();\r\n			$this->language->load(\'mpmultivendor/adminlink\');\r\n			if ($this->user->hasPermission(\'access\', \'mpmultivendor/mpmultivendor\')) {\r\n				$mpmultivendor[] = array(\r\n					\'name\'	   => $this->language->get(\'text_mpsetting\'),\r\n					\'href\'     => $this->url->link(\'mpmultivendor/mpmultivendor\', \'user_token=\' . $this->session->data[\'user_token\'], $ssl),\r\n					\'children\' => array()\r\n				);\r\n			}\r\n\r\n			if ($this->user->hasPermission(\'access\', \'mpmultivendor/mpseller\')) {\r\n				$mpmultivendor[] = array(\r\n					\'name\'	   => $this->language->get(\'text_mpseller\'),\r\n					\'href\'     => $this->url->link(\'mpmultivendor/mpseller\', \'user_token=\' . $this->session->data[\'user_token\'], $ssl),\r\n					\'children\' => array()\r\n				);\r\n			}\r\n\r\n			if ($this->user->hasPermission(\'access\', \'mpmultivendor/order\')) {\r\n				$mpmultivendor[] = array(\r\n					\'name\'	   => $this->language->get(\'text_seller_orders\'),\r\n					\'href\'     => $this->url->link(\'mpmultivendor/order\', \'user_token=\' . $this->session->data[\'user_token\'], $ssl),\r\n					\'children\' => array()\r\n				);\r\n			}\r\n\r\n			if ($this->user->hasPermission(\'access\', \'mpmultivendor/commission\')) {\r\n				$mpmultivendor[] = array(\r\n					\'name\'	   => $this->language->get(\'text_seller_commissions\'),\r\n					\'href\'     => $this->url->link(\'mpmultivendor/commission\', \'user_token=\' . $this->session->data[\'user_token\'], $ssl),\r\n					\'children\' => array()\r\n				);\r\n			}\r\n\r\n			if ($this->user->hasPermission(\'access\', \'mpmultivendor/payment\')) {\r\n				$mpmultivendor[] = array(\r\n					\'name\'	   => $this->language->get(\'text_seller_payments\'),\r\n					\'href\'     => $this->url->link(\'mpmultivendor/payment\', \'user_token=\' . $this->session->data[\'user_token\'], $ssl),\r\n					\'children\' => array()\r\n				);\r\n			}\r\n\r\n			if ($this->user->hasPermission(\'access\', \'mpmultivendor/product\')) {\r\n				$mpmultivendor[] = array(\r\n					\'name\'	   => $this->language->get(\'text_seller_products\'),\r\n					\'href\'     => $this->url->link(\'mpmultivendor/product\', \'user_token=\' . $this->session->data[\'user_token\'], $ssl),\r\n					\'children\' => array()\r\n				);\r\n			}\r\n\r\n			if ($this->user->hasPermission(\'access\', \'mpmultivendor/enquiries\')) {\r\n				$mpmultivendor[] = array(\r\n					\'name\'	   => $this->language->get(\'text_seller_enquiries\'),\r\n					\'href\'     => $this->url->link(\'mpmultivendor/enquiries\', \'user_token=\' . $this->session->data[\'user_token\'], $ssl),\r\n					\'children\' => array()\r\n				);\r\n			}\r\n\r\n			if ($this->user->hasPermission(\'access\', \'mpmultivendor/review\')) {\r\n				$mpmultivendor[] = array(\r\n					\'name\'	   => $this->language->get(\'text_seller_review\'),\r\n					\'href\'     => $this->url->link(\'mpmultivendor/review\', \'user_token=\' . $this->session->data[\'user_token\'], $ssl),\r\n					\'children\' => array()\r\n				);\r\n			}\r\n\r\n			if ($mpmultivendor) {\r\n				$data[\'menus\'][] = array(\r\n					\'id\'       => \'menu-mpmultivendor\',\r\n					\'icon\'	   => \'fa-users\', \r\n					\'name\'	   => $this->language->get(\'text_mpmultivendor\'),\r\n					\'href\'     => \'\',\r\n					\'children\' => $mpmultivendor\r\n				);		\r\n			}\r\n			/**** MP Seller Ends ****/\r\n			]]></add>\r\n		</operation>\r\n	</file>\r\n	<file path=\"admin/language/*/catalog/product.php\">\r\n		<operation error=\"skip\">\r\n			<search><![CDATA[// Help]]></search>\r\n			<add position=\"before\"><![CDATA[\r\n/* MP Seller Starts */\r\n$_[\'entry_mpseller\']			= \'Seller\';\r\n/* MP Seller Ends */\r\n				]]>\r\n			</add>\r\n		</operation>\r\n	</file>\r\n	<file path=\"admin/controller/catalog/product.php\">\r\n		<operation error=\"skip\">\r\n			<search><![CDATA[// Categories]]></search>\r\n			<add position=\"before\"><![CDATA[\r\n		/* MP Seller Starts */\r\n		$this->load->model(\'mpmultivendor/mpseller\');\r\n		$data[\'mpsellers\'] = $this->model_mpmultivendor_mpseller->getMpsellers();\r\n\r\n		$data[\'entry_mpseller\'] = $this->language->get(\'entry_mpseller\');\r\n\r\n		if (isset($this->request->post[\'mpseller_id\'])) {\r\n			$data[\'mpseller_id\'] = $this->request->post[\'mpseller_id\'];\r\n		} elseif (!empty($product_info)) {\r\n			$data[\'mpseller_id\'] = $product_info[\'mpseller_id\'];\r\n		} else {\r\n			$data[\'mpseller_id\'] = 0;\r\n		}\r\n		/* MP Seller Ends */\r\n				]]>\r\n			</add>\r\n		</operation>\r\n	</file>\r\n	<file path=\"admin/model/catalog/product.php\">\r\n		<operation error=\"skip\">\r\n			<search><![CDATA[if (isset($data[\'image\'])) {]]></search>\r\n			<add position=\"before\"><![CDATA[\r\n		/* MP Seller Starts */\r\n		if (isset($data[\'mpseller_id\'])) {\r\n			$this->db->query(\"UPDATE \" . DB_PREFIX . \"product SET mpseller_id = \'\" . $this->db->escape($data[\'mpseller_id\']) . \"\' WHERE product_id = \'\" . (int)$product_id . \"\'\");\r\n		}\r\n		/* MP Seller Ends */\r\n				]]>\r\n			</add>\r\n		</operation>\r\n	</file>\r\n	<file path=\"admin/view/template/catalog/product_form.twig\">\r\n		<operation error=\"skip\">\r\n			<search><![CDATA[<div class=\"tab-pane\" id=\"tab-links\">]]></search>\r\n			<add position=\"after\"><![CDATA[\r\n        	  <div class=\"form-group\">\r\n                <label class=\"col-sm-2 control-label\" for=\"input-mpseller\">{{ entry_mpseller }}</label>\r\n                <div class=\"col-sm-10\">\r\n                  <select name=\"mpseller_id\" class=\"form-control\">\r\n                  	<option value=\"\">{{ text_select }}</option>\r\n                  	{% for mpseller in mpsellers %}\r\n                  	{% if mpseller.mpseller_id == mpseller_id %}\r\n                  	<option value=\"{{ mpseller.mpseller_id }}\" selected=\"selected\">{{ mpseller.store_owner }}</option>\r\n                  	{% else %}\r\n                  	<option value=\"{{ mpseller.mpseller_id }}\">{{ mpseller.store_owner }}</option>\r\n                  	{% endif %}\r\n                  	{% endfor %}\r\n                  </select>\r\n                </div>\r\n              </div>\r\n				]]>\r\n			</add>\r\n		</operation>\r\n	</file>\r\n	<file path=\"system/library/cart/cart.php\">\r\n		<operation error=\"skip\">\r\n			<search><![CDATA[$product_data[] = array(]]></search>\r\n			<add position=\"after\"><![CDATA[\r\n					/* MP Seller Starts */\r\n					\'mpseller_id\'	  => isset($product_query->row[\'mpseller_id\']) ? $product_query->row[\'mpseller_id\'] : \'\',\r\n					/* MP Seller Ends */\r\n				]]>\r\n			</add>\r\n		</operation>\r\n	</file>\r\n	<file path=\"catalog/controller/common/header.php\">\r\n		<operation error=\"skip\">\r\n			<search><![CDATA[$data[\'cart\'] = $this->load->controller(\'common/cart\');]]></search>\r\n			<add position=\"after\"><![CDATA[\r\n			/* MP Seller Starts */\r\n			$this->load->language(\'mpmultivendor/product\');\r\n			$data[\'text_all_sellers\'] = $this->language->get(\'text_all_sellers\');\r\n			\r\n			$data[\'all_sellers\'] = $this->url->link(\'mpmultivendor/mv_seller\', \'\', true);\r\n\r\n			$data[\'mpmultivendor_status\'] = $this->config->get(\'mpmultivendor_status\');\r\n			/* MP Seller Ends */\r\n				]]>\r\n			</add>\r\n		</operation>\r\n	</file>\r\n	<file path=\"catalog/view/theme/*/template/common/header.twig\">\r\n		<operation error=\"skip\">\r\n			<search><![CDATA[<li><a href=\"{{ contact }}\">]]></search>\r\n			<add position=\"before\"><![CDATA[\r\n			{% if mpmultivendor_status %}\r\n			<li><a href=\"{{ all_sellers }}\" title=\"{{ text_all_sellers }}\"><i class=\"fa fa-users\"></i> <span class=\"hidden-xs hidden-sm hidden-md\">{{ text_all_sellers }}</span></a></li>\r\n			{% endif %}\r\n				]]>\r\n			</add>\r\n		</operation>\r\n	</file>\r\n	<file path=\"catalog/controller/product/product.php\">\r\n		<operation error=\"skip\">\r\n			<search><![CDATA[$this->model_catalog_product->updateViewed($this->request->get[\'product_id\']);]]></search>\r\n			<add position=\"after\"><![CDATA[\r\n			/* MP Seller Starts */\r\n			if($this->config->get(\'mpmultivendor_status\')) {\r\n				$this->load->language(\'mpmultivendor/product\');\r\n				$this->load->model(\'mpmultivendor/mv_seller\');\r\n\r\n				$data[\'text_soldby\'] = $this->language->get(\'text_soldby\');\r\n				$data[\'text_seller_rating\'] = $this->language->get(\'text_seller_rating\');\r\n				$data[\'text_seller_reviews\'] = $this->language->get(\'text_seller_reviews\');\r\n\r\n				$mpseller_info = $this->model_mpmultivendor_mv_seller->getSellerStoreInfo($product_info[\'mpseller_id\']);\r\n\r\n				if($mpseller_info) {\r\n					$data[\'isseller_store\'] = true;\r\n					$data[\'mpseller_store\'] = $mpseller_info[\'store_name\'];\r\n					$data[\'mpseller_rating\'] = $mpseller_info[\'rating\'];\r\n					$data[\'mpseller_href\'] = $this->url->link(\'mpmultivendor/store\', \'mpseller_id=\'. $mpseller_info[\'mpseller_id\'], true);\r\n\r\n					$data[\'mpseller_reviewshref\'] = $this->url->link(\'mpmultivendor/mv_reviews\', \'review_mpseller_id=\'. $mpseller_info[\'mpseller_id\'], true);\r\n\r\n					$mpseller_reviews = $this->model_mpmultivendor_mv_seller->getTotalReviews($product_info[\'mpseller_id\']);\r\n					\r\n					$data[\'mpseller_reviews\'] = $mpseller_reviews;\r\n				} else {\r\n					$data[\'isseller_store\'] = false;\r\n				}\r\n			} else {\r\n				$data[\'isseller_store\'] = false;\r\n			}\r\n			/* MP Seller Ends */\r\n				]]>\r\n			</add>\r\n		</operation>\r\n	</file>\r\n	<file path=\"catalog/view/theme/*/template/product/product.twig\">\r\n		<operation error=\"skip\">\r\n			<search><![CDATA[{{ stock }}</li>]]></search>\r\n			<add position=\"after\"><![CDATA[\r\n            {% if isseller_store %}\r\n            <li>{{ text_soldby }}: <a href=\"{{ mpseller_href }}\">{{ mpseller_store }}</a></li>\r\n            <li>\r\n              <div class=\"rating\">\r\n              {{ text_seller_rating }}: \r\n              {% for i in 1..5 %}\r\n              {% if mpseller_rating < i %}\r\n              <span class=\"fa fa-stack\"><i class=\"fa fa-star-o fa-stack-1x\"></i></span>\r\n              {% else %}\r\n              <span class=\"fa fa-stack\"><i class=\"fa fa-star fa-stack-1x\"></i><i class=\"fa fa-star-o fa-stack-1x\"></i></span>\r\n              {% endif %}\r\n              {% endfor %}\r\n              </div>      \r\n            </li>\r\n            <li>{{ text_seller_reviews }}: <a href=\"{{ mpseller_reviewshref }}\">({{ mpseller_reviews }})</a></li>\r\n            {% endif %}\r\n            <!-- MP Seller Ends -->\r\n				]]>\r\n			</add>\r\n		</operation>\r\n	</file>\r\n	<file path=\"catalog/view/theme/journal2/template/product/product.twig\">\r\n		<operation error=\"skip\">\r\n			<search><![CDATA[{{ stock }}</span></li>]]></search>\r\n			<add position=\"after\"><![CDATA[\r\n            {% if isseller_store %}\r\n            <li>{{ text_soldby }}: <a href=\"{{ mpseller_href }}\">{{ mpseller_store }}</a></li>\r\n            <li>\r\n              <div class=\"rating\">\r\n              {{ text_seller_rating }}: \r\n              {% for i in 1..5 %}\r\n              {% if mpseller_rating < i %}\r\n              &nbsp;<span class=\"fa fa-stack\"><i class=\"fa fa-star-o fa-stack-1x\"></i></span>\r\n              {% else %}\r\n              &nbsp;<span class=\"fa fa-stack\"><i class=\"fa fa-star